import React,{useState, useEffect} from 'react';
import {View, Text,StyleSheet,Dimensions,TouchableOpacity,Image} from 'react-native';
import Constants from 'expo-constants';
import * as Location from 'expo-location';
import MapView from 'react-native-maps';
import * as ImagePicker from 'expo-image-picker';

export default function App(){
  const [location, setLocation] = useState(null);
  const [errorMsg, setErrorMsg] = useState(null);
  const [image, setImage] = useState('data:image/jpeg;base64,');
  const [markers,setMarkers] = useState([{coordinate: { latitude: 21.020019254261747 ,longitude: 105.7911284},title:'Center Point',description:'this is center point'}]);
 
  

  useEffect(() => {
    if (Platform.OS === 'android' && !Constants.isDevice) {
      setErrorMsg(
        'This will not work on Sketch in an Android emulator. Try it on your device!'
      );
    } else {
      (async () => {
        let { status } = await Location.requestPermissionsAsync();
        if (status !== 'granted') {
          setErrorMsg('Permission to access location was denied');
        }

        let location = await Location.getCurrentPositionAsync({});
        
        setLocation(location);
      })();
    }
  });


  const setNewMarker = async (e)=>{
    const coords = e.nativeEvent.coordinate;
    setMarkers([...markers,{coordinate:coords}]);
    console.log(coords);
  }

  useEffect(()=>{
    async ()=>{
    const {status} = await ImagePicker.requestCameraRollPermissionsAsync();
    if (status !== 'granted') {
      setErrorMsg('Permission to access location was denied');
    }
  }
});

  let img='data:image/jpeg;base64,';
  const _pickImage = async () => {
   let result = await ImagePicker.launchImageLibraryAsync({
     mediaTypes: ImagePicker.MediaTypeOptions.Images,
     allowsEditing:true,
     aspect: [4,3],
     quality:1,
   });
   if(result.cancelled===false) {
     setImage(JSON.stringify(result.uri));
     img=result.uri;
   }

   console.log('result',result.uri);
   console.log('image',img);
  }

 

  return (
    <View style={styles.container} >
        <MapView style={styles.mapStyle}
          //provider = 'google'
          initialRegion = {{
            latitude: 21.020019254261747 ,
            longitude: 105.7911284,
            latitudeDelta:0.04,
            longitudeDelta:0.04
          }}
          onLongPress = {(e)=>setNewMarker(e)}
        >
          
            {markers.map((marker,id) =>
            <MapView.Marker
              key={id}
              coordinate = {marker.coordinate}
              title = {marker.title}
              description = {marker.description}
            >
            <MapView.Callout  onPress = {_pickImage} tooltip={true} >
              <View style={{backgroundColor:'#fff',borderRadius:20}}>
              <Image 
                style={{height:100,aspectRatio:4/3,resizeMode:'cover'}}
                source={{uri:img}}/>
                
        
            {marker.description?
              <Text style={{textAlign:'center'}}>
                {marker.description}
              </Text>
              :
              <Text style={{textAlign:'center'}}>
                {marker.coordinate.latitude} {marker.coordinate.longitude}
              </Text>
              }
              </View>
            </MapView.Callout>
          </MapView.Marker>
          )}
         
            
        </MapView>

      
    </View>
);
}

const styles = StyleSheet.create({
container: {
  flex: 1,
  backgroundColor: '#fff',
  alignItems: 'center',
  justifyContent: 'center',
},
mapStyle: {
  width: Dimensions.get('window').width,
  height: Dimensions.get('window').height,
},
});